mapper.select:
select
id, menu_name, parent_id, menu_sort, menu_type, admin_role, hotel_id, createor, create_time, updater,
update_time, state, remark, deleted
from hotel_dev.menu


        and id = #{id}


        and menu_name = #{menuName}


        and parent_id = #{parentId}


        and menu_sort = #{menuSort}


        and menu_type = #{menuType}


        and admin_role = #{adminRole}


        and hotel_id = #{hotelId}


        and createor = #{createor}


        and create_time = #{createTime}


        and updater = #{updater}


        and update_time = #{updateTime}


        and state = #{state}


        and remark = #{remark}


        and deleted = #{deleted}
