mapper.select:
select
id, type_id, type_name, user_id, username, phone, hotel_id, room_type_id, room_type_name, room_id, num_of_room,
order_days, order_date, order_state, order_cost, createor, create_time, updater, update_time, state, remark,
deleted
from hotel_dev.orders


        and id = #{id}


        and type_id = #{typeId}


        and type_name = #{typeName}


        and user_id = #{userId}


        and username = #{username}


        and phone = #{phone}


        and hotel_id = #{hotelId}


        and room_type_id = #{roomTypeId}


        and room_type_name = #{roomTypeName}


        and room_id = #{roomId}


        and num_of_room = #{numOfRoom}


        and order_days = #{orderDays}


        and order_date = #{orderDate}


        and order_state = #{orderState}


        and order_cost = #{orderCost}


        and createor = #{createor}


        and create_time = #{createTime}


        and updater = #{updater}


        and update_time = #{updateTime}


        and state = #{state}


        and remark = #{remark}


        and deleted = #{deleted}
