mapper.select:
select
id, number, type_id, type_name, price, discount, createor, create_time, updater, update_time, state, remark,
deleted
from hotel_dev.room


        and id = #{id}


        and number = #{number}


        and type_id = #{typeId}


        and type_name = #{typeName}


        and price = #{price}


        and discount = #{discount}


        and createor = #{createor}


        and create_time = #{createTime}


        and updater = #{updater}


        and update_time = #{updateTime}


        and state = #{state}


        and remark = #{remark}


        and deleted = #{deleted}
