mapper.select:
select
id, type_name, area, bed_num, bed_size, window, price, discount, createor, create_time, updater, update_time,
state, remark, deleted
from hotel_dev.room_type


        and id = #{id}


        and type_name = #{typeName}


        and area = #{area}


        and bed_num = #{bedNum}


        and bed_size = #{bedSize}


        and window = #{window}


        and price = #{price}


        and discount = #{discount}


        and createor = #{createor}


        and create_time = #{createTime}


        and updater = #{updater}


        and update_time = #{updateTime}


        and state = #{state}


        and remark = #{remark}


        and deleted = #{deleted}
