mapper.select:
select
id, username, password, nickname, real_name, birth, sign, icon, gender, phone, email, address, id_card,
createor, create_time, updater, update_time, state, remark, deleted
from hotel_dev.user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and nickname = #{nickname}


        and real_name = #{realName}


        and birth = #{birth}


        and sign = #{sign}


        and icon = #{icon}


        and gender = #{gender}


        and phone = #{phone}


        and email = #{email}


        and address = #{address}


        and id_card = #{idCard}


        and createor = #{createor}


        and create_time = #{createTime}


        and updater = #{updater}


        and update_time = #{updateTime}


        and state = #{state}


        and remark = #{remark}


        and deleted = #{deleted}
